import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
`;

export const Form = styled.form`
  width: 400px;
  background: #fff;
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  img {
    width: 100px;
    margin: 10px 0 40px;
  }
  p {
    color: #ff3333;
    margin-bottom: 15px;
    border: 1px solid #ff3333;
    padding: 10px;
    width: 100%;
    text-align: center;
  }
  input {
    size: 20px;
    width: 579px;
    height: 0px;
    left: 431px;
    top: 462px;
    margin-bottom: 50px;
    border: 1px solid #138A72;
    
    border-right: 0px;
    border-left: 0px;
    border-top: 0px;
    }
  }
  button {
    width: 579px;
    height: 57px;
    left: 431px;
    top: 799px;

    background: #138A72;
    border-radius: 100px;
  }
  hr {
    margin: 20px 0;
    border: none;
    border-bottom: 1px solid #cdcdcd;
    width: 100%;
  }
  a {
    font-size: 16;
    font-weight: bold;
    color: #999;
    text-decoration: none;
  }
`;
